package gui;

import java.awt.Component;

import javax.swing.JTable;
import javax.swing.table.DefaultTableCellRenderer;

// TODO: Auto-generated Javadoc
/**
 * The Class DetailsTableCellRenderer.
 */
public class DetailsTableCellRenderer extends DefaultTableCellRenderer {

	/** The Constant serialVersionUID. */
	private static final long serialVersionUID = -9091043012599256690L;

	/**
	 * Instantiates a new details table cell renderer.
	 */
	public DetailsTableCellRenderer() {

		super();
	}

	/**
	 * Gets the table cell renderer component.
	 *
	 * @param table the table
	 * @param value the value
	 * @param isSelected the is selected
	 * @param hasFocus the has focus
	 * @param row the row
	 * @param column the column
	 * @return the table cell renderer component
	 */
	public Component getTableCellRendererComponent(JTable table, String value,
			boolean isSelected, boolean hasFocus, int row, int column) {


		setText(value != null ? value : "");
		return this;
	}
}
